"use strict";(self.webpackChunkwoodpecker=self.webpackChunkwoodpecker||[]).push([["1964"],{7389:function(t){t.exports=JSON.parse('{"name":"Home Assistant Notify","url":"https://github.com/DHandspikerWade/woodpecker-plugin-ha-notify","description":"Plugin to send notifications of pipeline status via Home Assistant","docs":"<h1>Home Assistant Notify</h1>\\n<p>Plugin to send notifications of pipeline status via Home Assistant.</p>\\n<h2>Settings</h2>\\n<table>\\n<thead>\\n<tr>\\n<th>Settings Name</th>\\n<th>Required</th>\\n<th>Description</th>\\n</tr>\\n</thead>\\n<tbody><tr>\\n<td><code>host</code></td>\\n<td><strong>Yes</strong></td>\\n<td>Home Assistant hostname and optional port without protocol</td>\\n</tr>\\n<tr>\\n<td><code>token</code></td>\\n<td><strong>Yes</strong></td>\\n<td>A long-lived access token [<a href=\\"https://developers.home-assistant.io/docs/auth_api/#long-lived-access-token\\">Official Documentation</a>]</td>\\n</tr>\\n<tr>\\n<td><code>notify_id</code></td>\\n<td><strong>Yes</strong></td>\\n<td>ID notify service to use. (Ex: <code>mobile_app_my_phone</code>) [<a href=\\"https://www.home-assistant.io/integrations/notify/\\">Official Documentation</a>]</td>\\n</tr>\\n<tr>\\n<td><code>success_message</code></td>\\n<td>No</td>\\n<td>Optional. Message to display on success</td>\\n</tr>\\n<tr>\\n<td><code>failure_message</code></td>\\n<td>No</td>\\n<td>Optional. Message to display on failure</td>\\n</tr>\\n<tr>\\n<td><code>status</code></td>\\n<td>Depends</td>\\n<td>Optional but required for Woodpecker &gt;3.0 due to <a href=\\"https://github.com/woodpecker-ci/woodpecker/issues/4337\\">woodpecker-ci/woodpecker#4337</a>. Workflow status override</td>\\n</tr>\\n</tbody></table>\\n<h2>Usage</h2>\\n<pre><code class=\\"language-yaml\\">steps:\\n  - name: Send Notification\\n    image: ghcr.io/dhandspikerwade/woodpecker-plugin-ha-notify:1\\n    settings:\\n      host:\\n        from_secret: homeassistant_host\\n      token:\\n        from_secret: homeassistant_token\\n      notify_id:\\n        from_secret: homeassistant_notify\\n      status: failure\\n    when:\\n      - event: [ tag ]\\n</code></pre>\\n<h2>Examples</h2>\\n<h3>Successful Notification</h3>\\n<p><img alt=\\"sucess\\" src=\\"./img/success.png\\"></p>\\n<h3>Failed Notification</h3>\\n<p><img alt=\\"failed\\" src=\\"./img/failed.png\\"></p>\\n","tags":["notifications"],"author":"DHandspikerWade","containerImage":"ghcr.io/dhandspikerwade/woodpecker-plugin-ha-notify:1","containerImageUrl":"https://github.com/DHandspikerWade/woodpecker-plugin-ha-notify/pkgs/container/woodpecker-plugin-ha-notify","verified":false}')}}]);