"use strict";(self.webpackChunkwoodpecker=self.webpackChunkwoodpecker||[]).push([["3066"],{96975:function(e){e.exports=JSON.parse('{"name":"Teams Notify","slug":"teams-notify","url":"https://github.com/GECO-IT/woodpecker-plugin-teams-notify","icon":"https://upload.wikimedia.org/wikipedia/commons/thumb/c/c9/Microsoft_Office_Teams_%282018%E2%80%93present%29.svg/langfr-110px-Microsoft_Office_Teams_%282018%E2%80%93present%29.svg.png","description":"Plugin to send pipeline status notify to Microsot Teams","docs":"<h1>Teams Notify</h1>\\n<p>Plugin for sending Microsoft Teams notifications with pipeline status.</p>\\n<p>For create an Teams Webhook follow <a href=\\"https://learn.microsoft.com/en-us/microsoftteams/platform/webhooks-and-connectors/how-to/add-incoming-webhook\\">this</a></p>\\n<h2>Settings</h2>\\n<table>\\n<thead>\\n<tr>\\n<th>Settings Name</th>\\n<th>Default</th>\\n<th>Description</th>\\n</tr>\\n</thead>\\n<tbody><tr>\\n<td><code>url</code></td>\\n<td><em>none</em></td>\\n<td>Teams Webhook URL</td>\\n</tr>\\n<tr>\\n<td><code>private_forge</code></td>\\n<td><em>false</em></td>\\n<td>Set this to true if your forge is not accessible to the world</td>\\n</tr>\\n<tr>\\n<td><code>debug</code></td>\\n<td><em>false</em></td>\\n<td>Enable Debug mode</td>\\n</tr>\\n</tbody></table>\\n<h2>Pipeline Usage</h2>\\n<pre><code class=\\"language-yaml\\">...\\nsteps:\\n  teams-notify:\\n    image: gecoit84/woodpecker-teams-notify-plugin\\n    settings:\\n      url:\\n        from_secret: teams_webhook\\n    when:\\n      - status: [success, failure]\\n        event: [push, tag]\\n...\\n</code></pre>\\n<h2>Result</h2>\\n<ul>\\n<li>Success Build Notification</li>\\n</ul>\\n<p><img src=\\"./assets/success.png\\" alt=\\"sucess\\"></p>\\n<ul>\\n<li>Failed Build Notification</li>\\n</ul>\\n<p><img src=\\"./assets/failed.png\\" alt=\\"failed\\"></p>\\n","tags":["notifications","chat"],"author":"Geco-iT Teams","containerImage":"gecoit84/woodpecker-teams-notify-plugin","containerImageUrl":"https://hub.docker.com/r/gecoit84/woodpecker-teams-notify-plugin","verified":false}')}}]);